package `in`.co.ngcapp.customaccount.adapterimport `in`.co.ngcapp.customaccount.Rimport `in`.co.ngcapp.model.UserDataimport android.content.Contextimport android.os.Parcelimport android.os.Parcelableimport android.support.v7.widget.RecyclerViewimport android.util.Base64import android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport com.bumptech.glide.Glideimport de.hdodenhof.circleimageview.CircleImageViewclass CustomerList(var mContext: Context, var arraList: ArrayList<UserData>) :    RecyclerView.Adapter<CustomerList.CustomHolder>() {    override fun onCreateViewHolder(parent: ViewGroup?, viewType: Int): CustomerList.CustomHolder {        var view = LayoutInflater.from(parent!!.context).inflate(R.layout.customer_adapters, parent, false)        return CustomHolder(view)    }    override fun getItemCount(): Int {        return arraList.size    }    override fun onBindViewHolder(holder: CustomerList.CustomHolder?, position: Int) {        holder!!.holdData(mContext, arraList[position])    }    class CustomHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        fun holdData(mContext: Context, arraList: UserData) {            var profile_image = itemView.findViewById<CircleImageView>(R.id.customer_images)            var phone_number = itemView.findViewById<TextView>(R.id.phone_number)            var name = itemView.findViewById<TextView>(R.id.name)            var dateOfBirth = itemView.findViewById<TextView>(R.id.date_of_birth)            var addresss     = itemView.findViewById<TextView>(R.id.address)            var salary    = itemView.findViewById<TextView>(R.id.salary)            phone_number.text = "Phone Number : " + arraList.mPhoneNumber            name.text = "Name : " + arraList.mName            dateOfBirth.text = "Date Of Birth : " + arraList.mDateofBirth            addresss.text    = "Address :" +arraList.mAddress            salary.text      = "Salary :" +arraList.inComeSlab            Glide.with(mContext)                .load(Base64.decode(arraList.imagesCustomer, Base64.DEFAULT))                .asBitmap()                .placeholder(R.drawable.ic_account_circle_black_24dp)                .into(profile_image)        }    }}